import React, { Component } from 'react';
import './index.css';
import logo from './assets/images/logo.png';
import axios from 'axios';
import { render } from 'react-dom';
/* import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheckSquare, faCoffee } from '@fortawesome/free-solid-svg-icons' */
import {Router, Route} from 'react-router';

import { createBrowserHistory } from "history";




class App extends Component {
  constructor(props) {
    super(props);
    this.state = {nameValue: '', passValue: ''};    
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  getData() {
    // create a new XMLHttpRequest
    /* var xhr = new XMLHttpRequest()

    // get a callback when the server responds
    xhr.addEventListener('load', () => {
      // update the state of the component with the result here
      console.log(xhr.responseText)
    })
    // open the request with the verb and the url
    xhr.open('POST', 'https://dog.ceo/api/breeds/list/all')
    // send the request
    xhr.send(JSON.stringify({ data: {"Password": "ADM2019", "UserName": "77522330-8"} })) */
    console.log('hola');    
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }

  async handleSubmit(event) {
    /* alert('A name was submitted: ' + this.state.nameValue + this.state.passValue); */
    event.preventDefault();
    const response = await axios.get(
      'http://conectadev.portal.vsoft.cl/api/jsonws/vs-bpm-screen-portlet.customize/get-list-obras',
      { data: {companyId: 25728, groupId: 25754, userId: 223645} },      
      { headers: {'Authorization': 'UwnjnP6AbTQsc6bN9cx7', /* "access-control-allow-origin": "origin", */ 'Access-Control-Allow-Origin': "*", 'Content-Type': 'application/json'} }
    ).then((response) => {
      console.log(response.data)  
    })          
  }

  render() {
      return (
      <div className="fondo-app-login flex">
          <div>
            <img src={logo}/>             
          </div>       
          <form className='flex' onSubmit={this.handleSubmit}>
            <label><input className='input' name='nameValue' type="text" /* style={{fontFamily:FontAwesome}} */ value={this.state.nameValue} placeholder='Nombre usuario' onChange={this.handleChange} /></label>
            <label><input className='input' name='passValue' type="password" value={this.state.passValue} placeholder='Clave' onChange={this.handleChange} /></label>

            <input type="submit" value="Login" />
            {/* <Link to='/solicitudes'>Login</Link> */}
          </form>   
      </div>
      );
  }
}

render(
  <Router>      
      <Route path="/" component={App}/>
  </Router>,
  document.getElementById('root')
);
