import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import logo from './assets/images/logo.png';

class App extends React.Component {
  render() {
      return (
      <div className="fondo-app">
          <div className="fondo-app__app-bar flex">
            <img src={logo}/>
          </div>
          <div className="game-info">
          <div>{/* status */}</div>
          <ol>{/* TODO */}</ol>
          </div>
      </div>
      );
  }
}

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {nameValue: '', passValue: ''};    
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  getData() {
    // create a new XMLHttpRequest
    /* var xhr = new XMLHttpRequest()

    // get a callback when the server responds
    xhr.addEventListener('load', () => {
      // update the state of the component with the result here
      console.log(xhr.responseText)
    })
    // open the request with the verb and the url
    xhr.open('POST', 'https://dog.ceo/api/breeds/list/all')
    // send the request
    xhr.send(JSON.stringify({ data: {"Password": "ADM2019", "UserName": "77522330-8"} })) */
    console.log('hola');    
  }

  handleChange(event) {
    this.setState({ [event.target.name]: event.target.value });
  }
  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.nameValue + this.state.passValue);
    console.log('hola');    
    event.preventDefault();
  }

  render() {
      return (
      <div className="fondo-app-login flex">
          <div>
            <img src={logo}/>             
          </div>       
          <form className='flex' onSubmit={this.getData}>
            <label><input name='nameValue' type="text" value={this.state.nameValue} placeholder='Nombre Usuario' onChange={this.handleChange} /></label>
            <label><input name='passValue' type="text" value={this.state.passValue} placeholder='Clave' onChange={this.handleChange} /></label>
            <input type="submit" value="Submit" />
          </form>   
      </div>
      );
  }
}

ReactDOM.render(
    <Login />,
    document.getElementById('root')
);
